mapper.select:
SELECT id, parent_id, name, path, component, permission, type, icon, sort, created_at, updated_at
FROM menu
WHERE id = #{id}

mapper.update:
UPDATE menu
parent_id = #{parentId}, name = #{name}, path = #{path}, component = #{component}, permission = #{permission}, type = #{type}, icon = #{icon}, sort = #{sort},
WHERE id = #{id}
