mapper.insert:
INSERT INTO biz_purchase_order (
    order_no, spare_part_id, supplier_id, order_qty, order_status,
    expected_date, reorder_suggest_id, purchaser_id, remark
) VALUES (
    #{orderNo}, #{sparePartId}, #{supplierId}, #{orderQty}, #{orderStatus},
    #{expectedDate}, #{reorderSuggestId}, #{purchaserId}, #{remark}
)

mapper.select:
SELECT o.id, o.order_no as orderNo, o.spare_part_id as sparePartId, o.supplier_id as supplierId, o.order_qty as orderQty, o.unit_price as unitPrice, o.total_amount as totalAmount, o.order_status as orderStatus, o.expected_date as expectedDate, o.actual_date as actualDate, o.reorder_suggest_id as reorderSuggestId, o.purchaser_id as purchaserId, o.remark, o.created_at as createdAt, o.updated_at as updatedAt, sp.code as sparePartCode, sp.name as sparePartName, s.name as supplierName, u.name as purchaserName FROM biz_purchase_order o LEFT JOIN spare_part sp ON o.spare_part_id = sp.id LEFT JOIN supplier s ON o.supplier_id = s.id LEFT JOIN user u ON o.purchaser_id = u.id
AND o.order_status = #{orderStatus}
AND o.spare_part_id = #{sparePartId}
AND o.supplier_id = #{supplierId}
ORDER BY o.created_at DESC

mapper.update:
UPDATE biz_purchase_order
SET unit_price = #{unitPrice}, total_amount = #{totalAmount}, order_status = #{orderStatus}
WHERE id = #{id}

UPDATE biz_purchase_order
SET order_status = #{orderStatus}, actual_date = #{actualDate}, remark = #{remark}
WHERE id = #{id}
