mapper.select:
SELECT s.id, s.part_code as partCode, s.suggest_month as suggestMonth, s.current_stock as currentStock, s.reorder_point as reorderPoint, s.suggest_qty as suggestQty, s.forecast_qty as forecastQty, s.lower_bound as lowerBound, s.upper_bound as upperBound, s.urgency, s.status, s.created_at as createdAt, s.updated_at as updatedAt, sp.name as sparePartName FROM biz_reorder_suggest s LEFT JOIN spare_part sp ON s.part_code = sp.code
AND s.status = #{status}
ORDER BY FIELD(s.urgency,'紧急','正常'), s.created_at DESC
