mapper.insert:
INSERT INTO biz_requisition (
    req_no, applicant_id, work_order_no, device_id, req_status, is_urgent, remark
) VALUES (
    #{reqNo}, #{applicantId}, #{workOrderNo}, #{deviceId}, #{reqStatus}, #{isUrgent}, #{remark}
)

mapper.select:
SELECT r.id, r.req_no as reqNo, r.applicant_id as applicantId, r.work_order_no as workOrderNo, r.device_id as deviceId, r.req_status as reqStatus, r.is_urgent as isUrgent, r.approve_id as approveId, r.approve_time as approveTime, r.approve_remark as approveRemark, r.apply_time as applyTime, r.remark, r.created_at as createdAt, r.updated_at as updatedAt, u.name as applicantName, a.name as approverName, e.name as deviceName, e.code as deviceCode FROM biz_requisition r LEFT JOIN user u ON r.applicant_id = u.id LEFT JOIN user a ON r.approve_id = a.id LEFT JOIN equipment e ON r.device_id = e.id
AND r.req_status = #{status}
AND r.applicant_id = #{applicantId}
ORDER BY r.is_urgent DESC, r.created_at DESC

mapper.update:
UPDATE biz_requisition 
SET req_status = #{status}, approve_id = #{approveId}, approve_remark = #{approveRemark}, approve_time = NOW()
WHERE id = #{id}
