mapper.select:
SELECT id, code, name, unified_social_credit_code, bank_account_info, contact_person, phone, address, status, remark, created_at, updated_at
FROM supplier
WHERE id = #{id}

mapper.update:
UPDATE supplier
code = #{code}, name = #{name}, unified_social_credit_code = #{unifiedSocialCreditCode}, bank_account_info = #{bankAccountInfo}, contact_person = #{contactPerson}, phone = #{phone}, address = #{address}, status = #{status}, remark = #{remark},
WHERE id = #{id}
